mapper.select:
SELECT PSEQ, ID, NAME, TYPECLASS, INFO, DELFLAG 
FROM PROJECT WHERE DELFLAG = 'N' AND ID = #{id}
ORDER BY 1

SELECT PSEQ, ID, NAME, TYPECLASS, INFO, DELFLAG 
FROM PROJECT
WHERE PSEQ = #{pseq}

SELECT PSEQ, ID, NAME, TYPECLASS, INFO, DELFLAG 
FROM PROJECT
WHERE PSEQ = #{pseq} AND ID = #{id}
